    private void listaEquipos(){
        Crud crud = new Crud("Equipos.txt");//Este es una clase que gestiona los datos(elimniar,crea,etc)
        ArrayList<String> equipos = crud.listaEquipos();
        if (equipos.size()>0) {
            for(String item:equipos){
                visitante.addItem(item);
                local.addItem(item);
            }
        }
    }
