    private void generarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_generarActionPerformed
        // TODO add your handling code here:
        try {
            Equipo equipo = new Equipo();
            equipo.id = id;
            equipo.local = local.getSelectedItem().toString();
            equipo.visitante = visitante.getSelectedItem().toString();
            equipo.fecha = fecha.getText();
            equipo.golesLocal = Integer.parseInt(golesLocal.getText());
            equipo.golesVisitante = Integer.parseInt(golesVisitante.getText());
            datos.add(equipo);

            String[] cabeceras = {"Id","Fecha encuentro","Local","Goles local","Visitante","Goles Visitante"};
            DefaultTableModel dt = new DefaultTableModel();
            dt.setColumnIdentifiers(cabeceras);
            
            Object[] cell = new Object[dt.getColumnCount()];
            for(Equipo item: datos){
                cell[0] = item.id;
                cell[1] = item.fecha;
                cell[2] = item.local;
                cell[3] = item.golesLocal;
                cell[4] = item.visitante;
                cell[5] = item.golesVisitante;
                dt.addRow(cell);
            }
            Crud crud = new Crud("Resultados.txt");
            String data = fecha.getText()+","+local.getSelectedItem()+","+golesLocal.getText()+","+visitante.getSelectedItem()+","+golesVisitante.getText();
            crud.Add(data);
            jTable1.setModel(dt);
            id++;
        } catch (Exception e) {
            return;
        }
        
    }//GEN-LAST:event_generarActionPerformed

